declare module 'react-native-usage-stats' {
  export type UsageStatsInfo = {
    firstTimeStamp: number
    lastTimeStamp: number
    lastTimeUsed: number
    packageName: string
    usageTime: number
  }

  export type UsageStats = {
    lastTimeForegroundServiceUsed?: number
    lastTimeVisible?: number
    totalTimeForegroundServiceUsed?: number
    totalTimeVisible?: number
    
    packageName: string
    firstTimeStamp: number
    lastTimeStamp: number
    lastTimeUsed: number
    totalTimeInForeground: number
  }

  const usageStats: {
    /**
     * For Android Only
     */
    checkPermission(): Promise<boolean>
    openUsageAccessSettings(packageName: string): Promise<void>,
    getAppUsageInfo(): Promise<UsageStatsInfo[]>
    getAppUsageStats(packageName: string, beginTime: number, endTime: number): Promise<UsageStatsInfo | null>
    getAppListUsageStats(packageNames: string[], beginTime: number, endTime: number): Promise<UsageStatsInfo[] | null>
    openApp(packageName: string): Promise<void>,
    openAppList(packageNames: string[]): Promise<void>,
    openNotificationSettings(): void
    queryUsageStats(beginTime: number, endTime: number): Promise<UsageStats[] | null>
    queryAndAggregateUsageStats(beginTime: number, endTime: number): Promise<Map<string, UsageStats> | null>
  }

  export default usageStats
}
